import { createSlice } from "@reduxjs/toolkit";

// a slice is logic containing reducers and actions

const initialState = {
  items: [],
  status: null,
};

const productsSlice = createSlice({
  name: "products",
  initialState,
  reducers: {},
});

export default productsSlice.reducer;
